//Acceso directo para que importe siempre el proceso de react
import React, { Component } from 'react';
import { Container, Form, Button, Row, Col } from 'react-bootstrap';

//marginTop: centrar el contenido en la pagina

//Constructores
export default class extends React.Component {
    constructor(props) {
        super(props);
    }
    state = {}
    render() {
        return (
            <Container id="login-container" style={{ marginTop: 200 }}>
                <Row>
                    <Col>
                        <Row>
                            <h2>Iniciar Sesion</h2>
                        </Row>
                        <Row>
                            <Col 
                            sm="12" 
                            xs="12"
                            md={{ span: 4, offset: 4}}
                            lg={{ span: 4, offset: 4}}
                            xl={{ span: 4, offset: 4}}>     
                                <Form>
                                    <Form.Group>
                                        <Form.Label style={{ float: 'left' }}>Nombre Usuario</Form.Label>
                                        <Form.Control 
                                            onChange={(e)=>
                                            this.setState({ usuario: e.target.value})}
                                            placeholder="Nombre Usuario" />
                                            
                                    </Form.Group>

                                    <Form.Group>
                                        <Form.Label style={{ float: 'left' }}>Contraseña</Form.Label>
                                        <Form.Control type="password" placeholder="Contraseña" />
                                    </Form.Group>

                                    <Button
                                        variant="primary"
                                        style={{ marginTop: 20, width: '100%' }}>
                                        Iniciar sesion
                                    </Button>
                                </Form>
                            </Col>
                        </Row>
                    </Col>
                </Row>
            </Container>
        );
    }
}
